import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { login } from '../services/api';
import './ModernAuth.css';

function Login() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await login(formData);
      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    window.location.href = 'http://localhost:5000/api/auth/google';
  };

  return (
    <div className="modern-auth-container">
      <div className="modern-auth-card">
        <h1 className="modern-auth-title">Welcome back</h1>
        <p className="modern-auth-subtitle">Welcome back! Please enter your details.</p>

        {error && <div className="modern-auth-error">{error}</div>}

        <form onSubmit={handleSubmit} className="modern-auth-form">
          <div className="modern-form-group">
            <label className="modern-label">Email</label>
            <input
              type="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              required
              className="modern-input"
            />
          </div>

          <div className="modern-form-group">
            <label className="modern-label">Password</label>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
              className="modern-input"
            />
          </div>

          <div className="modern-forgot">
            <a href="#forgot">Forgot password</a>
          </div>

          <button type="submit" className="modern-btn-primary" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>

          <button 
            type="button" 
            className="modern-btn-google"
            onClick={handleGoogleLogin}
          >
            <img 
              src="https://www.google.com/favicon.ico" 
              alt="Google" 
              className="google-icon"
            />
            Sign in with Google
          </button>
        </form>

        <div className="modern-auth-footer">
          Don't have an account? <Link to="/register">Sign up for free</Link>
        </div>
      </div>
    </div>
  );
}

export default Login;
