import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FaFacebook, FaTwitter, FaGoogle } from 'react-icons/fa';
import { login } from '../services/api';
import axios from 'axios';
import './GreenAuth.css';

function Login() {
  const navigate = useNavigate();
  const [showGoogleForm, setShowGoogleForm] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [googleData, setGoogleData] = useState({
    email: '',
    name: '',
    picture: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleGoogleChange = (e) => {
    setGoogleData({
      ...googleData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await login(formData);
      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    setShowGoogleForm(true);
  };

  const handleGoogleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/auth/google/callback', googleData);
      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (err) {
      setError('Google login failed');
      setLoading(false);
    }
  };

  const handleQuickLogin = async () => {
    const demoData = {
      email: 'demo@gmail.com',
      name: 'Demo User',
      picture: 'https://ui-avatars.com/api/?name=Demo+User&background=10b981&color=fff&size=200'
    };

    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/auth/google/callback', demoData);
      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (err) {
      setError('Quick login failed');
      setLoading(false);
    }
  };

  return (
    <div className="green-auth-container">
      {/* Left Side - Animated Background */}
      <div className="green-auth-left">
        <div className="green-leaves-bg">
          <div className="leaf leaf-1"></div>
          <div className="leaf leaf-2"></div>
          <div className="leaf leaf-3"></div>
          <div className="leaf leaf-4"></div>
        </div>
        <div className="green-content">
          <h1 className="green-title">Let's Get Started</h1>
          <p className="green-description">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
            Maecenas placerat ultricies libero eu pharetra. Vestibulum 
            ultricies augue.
          </p>
        </div>
        <div className="green-footer">
          <span>presented by</span>
          <div className="logo-placeholder">üé®</div>
        </div>
      </div>

      {/* Right Side - Form */}
      <div className="green-auth-right">
        {!showGoogleForm ? (
          <div className="green-form-container">
            <h2 className="form-title">Sign in</h2>

            {error && <div className="green-error">{error}</div>}

            <form onSubmit={handleSubmit} className="green-form">
              <div className="green-input-group">
                <input
                  type="email"
                  name="email"
                  placeholder="Your Email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  className="green-input"
                />
              </div>

              <div className="green-input-group">
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                  className="green-input"
                />
              </div>

              <button type="submit" className="green-btn-primary" disabled={loading}>
                {loading ? 'Signing in...' : 'Sign in'}
              </button>

              <div className="green-divider">
                <span>OR</span>
              </div>

              <div className="green-social-buttons">
                <button type="button" className="social-circle facebook">
                  <FaFacebook />
                </button>
                <button type="button" className="social-circle twitter">
                  <FaTwitter />
                </button>
                <button type="button" className="social-circle google" onClick={handleGoogleLogin}>
                  <FaGoogle />
                </button>
              </div>
            </form>

            <div className="green-auth-footer-text">
              Already a Member? <Link to="/register">Sign in here</Link>
            </div>
          </div>
        ) : (
          <div className="google-form-container">
            <img 
              src="https://www.google.com/images/branding/googlelogo/2x/googlelogo_color_272x92dp.png" 
              alt="Google" 
              className="google-logo"
            />
            <h2 className="google-title">Sign in with Google</h2>
            <p className="google-subtitle">Demo Mode - For Testing</p>

            {error && <div className="green-error">{error}</div>}

            <form onSubmit={handleGoogleSubmit} className="google-form">
              <div className="google-input-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  placeholder="your-email@gmail.com"
                  value={googleData.email}
                  onChange={handleGoogleChange}
                  required
                  className="google-input"
                />
              </div>

              <div className="google-input-group">
                <label>Name</label>
                <input
                  type="text"
                  name="name"
                  placeholder="Your Name"
                  value={googleData.name}
                  onChange={handleGoogleChange}
                  required
                  className="google-input"
                />
              </div>

              <div className="google-input-group">
                <label>Profile Picture URL (Optional)</label>
                <input
                  type="url"
                  name="picture"
                  placeholder="https://example.com/photo.jpg"
                  value={googleData.picture}
                  onChange={handleGoogleChange}
                  className="google-input"
                />
              </div>

              <button type="submit" className="google-btn-primary" disabled={loading}>
                {loading ? 'Signing in...' : 'Continue with Google'}
              </button>

              <button type="button" className="google-btn-secondary" onClick={handleQuickLogin}>
                Quick Login (Demo Account)
              </button>
            </form>

            <div className="google-back">
              <button onClick={() => setShowGoogleForm(false)}>‚Üê Back to Login</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Login;
