import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getProfile, updateProfile, uploadProfilePicture } from '../services/api';

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('profile');
  
  const [profileData, setProfileData] = useState({
    name: '',
    bio: '',
    github: '',
    linkedin: '',
    twitter: ''
  });
  
  const [skills, setSkills] = useState({
    frontend: '',
    backend: '',
    devops: '',
    tools: ''
  });
  
  const [project, setProject] = useState({
    title: '',
    description: '',
    techStack: '',
    repoLink: '',
    liveLink: ''
  });

    useEffect(() => {
    fetchProfile();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await getProfile();
      setUser(response.data);
      
      if (response.data.profile) {
        setProfileData({
          name: response.data.profile.name || '',
          bio: response.data.profile.bio || '',
          github: response.data.profile.socials?.github || '',
          linkedin: response.data.profile.socials?.linkedin || '',
          twitter: response.data.profile.socials?.twitter || ''
        });
      }
      
      if (response.data.skills) {
        setSkills({
          frontend: response.data.skills.frontend?.join(', ') || '',
          backend: response.data.skills.backend?.join(', ') || '',
          devops: response.data.skills.devops?.join(', ') || '',
          tools: response.data.skills.tools?.join(', ') || ''
        });
      }
      
      setLoading(false);
    } catch (error) {
      console.error('Error fetching profile:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      }
    }
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateProfile({
        profile: {
          name: profileData.name,
          bio: profileData.bio,
          socials: {
            github: profileData.github,
            linkedin: profileData.linkedin,
            twitter: profileData.twitter
          }
        }
      });
      alert('Profile updated successfully!');
      fetchProfile();
    } catch (error) {
      alert('Failed to update profile');
    }
  };

  const handleSkillsSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateProfile({
        skills: {
          frontend: skills.frontend.split(',').map(s => s.trim()).filter(s => s),
          backend: skills.backend.split(',').map(s => s.trim()).filter(s => s),
          devops: skills.devops.split(',').map(s => s.trim()).filter(s => s),
          tools: skills.tools.split(',').map(s => s.trim()).filter(s => s)
        }
      });
      alert('Skills updated successfully!');
      fetchProfile();
    } catch (error) {
      alert('Failed to update skills');
    }
  };

  const handleProjectSubmit = async (e) => {
    e.preventDefault();
    try {
      const newProject = {
        title: project.title,
        description: project.description,
        techStack: project.techStack.split(',').map(s => s.trim()).filter(s => s),
        repoLink: project.repoLink,
        liveLink: project.liveLink
      };
      
      const updatedProjects = [...(user.projects || []), newProject];
      
      await updateProfile({ projects: updatedProjects });
      alert('Project added successfully!');
      setProject({ title: '', description: '', techStack: '', repoLink: '', liveLink: '' });
      fetchProfile();
    } catch (error) {
      alert('Failed to add project');
    }
  };

  const handleTemplateChange = async (templateId) => {
    try {
      await updateProfile({ templateId });
      alert('Template updated successfully!');
      fetchProfile();
    } catch (error) {
      alert('Failed to update template');
    }
  };

  const handleProfilePictureUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('image', file);

    try {
      await uploadProfilePicture(formData);
      alert('Profile picture uploaded successfully!');
      fetchProfile();
    } catch (error) {
      alert('Failed to upload image');
      console.error(error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-xl text-gray-600">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
            <div className="flex gap-4">
              <a 
                href={`/${user.username}`} 
                target="_blank" 
                rel="noopener noreferrer"
                className="px-4 py-2 text-blue-600 hover:text-blue-800 font-medium"
              >
                View Portfolio â†’
              </a>
              <button 
                onClick={handleLogout}
                className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Tabs */}
        <div className="border-b border-gray-200 mb-8">
          <nav className="flex space-x-8">
            {['profile', 'projects', 'skills', 'template'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`py-4 px-1 border-b-2 font-medium text-sm capitalize transition ${
                  activeTab === tab
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab}
              </button>
            ))}
          </nav>
        </div>

        {/* Profile Tab */}
        {activeTab === 'profile' && (
          <div className="bg-white rounded-lg shadow-md p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Edit Profile</h2>
            
            <form onSubmit={handleProfileSubmit} className="space-y-6">
              {/* Profile Picture */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Profile Picture
                </label>
                {user.profile?.avatar && (
                  <img 
                    src={user.profile.avatar} 
                    alt="Profile" 
                    className="w-32 h-32 rounded-full object-cover mb-4 border-4 border-gray-200"
                  />
                )}
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleProfilePictureUpload}
                  className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Name</label>
                <input
                  type="text"
                  placeholder="Your Name"
                  value={profileData.name}
                  onChange={(e) => setProfileData({...profileData, name: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Bio</label>
                <textarea
                  placeholder="Tell us about yourself..."
                  value={profileData.bio}
                  onChange={(e) => setProfileData({...profileData, bio: e.target.value})}
                  rows="4"
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">GitHub URL</label>
                  <input
                    type="url"
                    placeholder="https://github.com/username"
                    value={profileData.github}
                    onChange={(e) => setProfileData({...profileData, github: e.target.value})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">LinkedIn URL</label>
                  <input
                    type="url"
                    placeholder="https://linkedin.com/in/username"
                    value={profileData.linkedin}
                    onChange={(e) => setProfileData({...profileData, linkedin: e.target.value})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Twitter URL</label>
                  <input
                    type="url"
                    placeholder="https://twitter.com/username"
                    value={profileData.twitter}
                    onChange={(e) => setProfileData({...profileData, twitter: e.target.value})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
              </div>

              <button 
                type="submit"
                className="w-full md:w-auto px-6 py-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition"
              >
                Save Profile
              </button>
            </form>
          </div>
        )}

        {/* Projects Tab */}
        {activeTab === 'projects' && (
          <div className="space-y-8">
            <div className="bg-white rounded-lg shadow-md p-8">
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Add New Project</h2>
              
              <form onSubmit={handleProjectSubmit} className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Project Title</label>
                  <input
                    type="text"
                    placeholder="My Awesome Project"
                    value={project.title}
                    onChange={(e) => setProject({...project, title: e.target.value})}
                    required
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Description</label>
                  <textarea
                    placeholder="Describe your project..."
                    value={project.description}
                    onChange={(e) => setProject({...project, description: e.target.value})}
                    rows="4"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Tech Stack</label>
                  <input
                    type="text"
                    placeholder="React, Node.js, MongoDB (comma separated)"
                    value={project.techStack}
                    onChange={(e) => setProject({...project, techStack: e.target.value})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">GitHub Repository</label>
                    <input
                      type="url"
                      placeholder="https://github.com/username/repo"
                      value={project.repoLink}
                      onChange={(e) => setProject({...project, repoLink: e.target.value})}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Live Demo URL</label>
                    <input
                      type="url"
                      placeholder="https://myproject.com"
                      value={project.liveLink}
                      onChange={(e) => setProject({...project, liveLink: e.target.value})}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>
                </div>

                <button 
                  type="submit"
                  className="w-full md:w-auto px-6 py-3 bg-green-600 text-white font-medium rounded-lg hover:bg-green-700 transition"
                >
                  Add Project
                </button>
              </form>
            </div>

            {/* Projects List */}
            <div className="bg-white rounded-lg shadow-md p-8">
              <h3 className="text-xl font-bold text-gray-900 mb-4">Your Projects</h3>
              {user.projects && user.projects.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {user.projects.map((proj, index) => (
                    <div key={index} className="border border-gray-200 rounded-lg p-6 hover:shadow-lg transition">
                      <h4 className="text-lg font-semibold text-gray-900 mb-2">{proj.title}</h4>
                      <p className="text-gray-600 mb-4">{proj.description}</p>
                      {proj.techStack && (
                        <div className="flex flex-wrap gap-2 mb-4">
                          {proj.techStack.map((tech, i) => (
                            <span key={i} className="px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                              {tech}
                            </span>
                          ))}
                        </div>
                      )}
                      <div className="flex gap-4">
                        {proj.repoLink && (
                          <a href={proj.repoLink} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:text-blue-800 font-medium">
                            GitHub â†’
                          </a>
                        )}
                        {proj.liveLink && (
                          <a href={proj.liveLink} target="_blank" rel="noopener noreferrer" className="text-green-600 hover:text-green-800 font-medium">
                            Live Demo â†’
                          </a>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500">No projects added yet.</p>
              )}
            </div>
          </div>
        )}

        {/* Skills Tab */}
        {activeTab === 'skills' && (
          <div className="bg-white rounded-lg shadow-md p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Edit Skills</h2>
            
            <form onSubmit={handleSkillsSubmit} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Frontend Skills</label>
                <input
                  type="text"
                  placeholder="React, JavaScript, HTML, CSS (comma separated)"
                  value={skills.frontend}
                  onChange={(e) => setSkills({...skills, frontend: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Backend Skills</label>
                <input
                  type="text"
                  placeholder="Node.js, Express, MongoDB (comma separated)"
                  value={skills.backend}
                  onChange={(e) => setSkills({...skills, backend: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">DevOps Skills</label>
                <input
                  type="text"
                  placeholder="Docker, AWS, CI/CD (comma separated)"
                  value={skills.devops}
                  onChange={(e) => setSkills({...skills, devops: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-yellow-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Tools</label>
                <input
                  type="text"
                  placeholder="Git, VS Code, Postman (comma separated)"
                  value={skills.tools}
                  onChange={(e) => setSkills({...skills, tools: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                />
              </div>

              <button 
                type="submit"
                className="w-full md:w-auto px-6 py-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition"
              >
                Save Skills
              </button>
            </form>
          </div>
        )}

        {/* Template Tab */}
        {activeTab === 'template' && (
          <div className="bg-white rounded-lg shadow-md p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Choose Template</h2>
            <p className="text-gray-600 mb-8">
              Current Template: <span className="font-semibold text-blue-600">{user.templateId || 'minimalist'}</span>
            </p>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div 
                onClick={() => handleTemplateChange('minimalist')}
                className={`cursor-pointer rounded-lg p-6 text-center transition transform hover:scale-105 ${
                  user.templateId === 'minimalist' 
                    ? 'border-4 border-blue-500 shadow-lg' 
                    : 'border-2 border-gray-200 hover:border-gray-300'
                }`}
              >
                <h3 className="text-xl font-bold text-gray-900 mb-2">Minimalist</h3>
                <p className="text-gray-600 text-sm">Clean & Simple</p>
              </div>
              
              <div 
                onClick={() => handleTemplateChange('cyberpunk')}
                className={`cursor-pointer rounded-lg p-6 text-center transition transform hover:scale-105 bg-black ${
                  user.templateId === 'cyberpunk' 
                    ? 'border-4 border-blue-500 shadow-lg' 
                    : 'border-2 border-gray-700 hover:border-gray-600'
                }`}
              >
                <h3 className="text-xl font-bold text-green-400 mb-2">Cyberpunk</h3>
                <p className="text-pink-400 text-sm">Dark & Neon</p>
              </div>
              
              <div 
                onClick={() => handleTemplateChange('corporate')}
                className={`cursor-pointer rounded-lg p-6 text-center transition transform hover:scale-105 bg-slate-700 ${
                  user.templateId === 'corporate' 
                    ? 'border-4 border-blue-500 shadow-lg' 
                    : 'border-2 border-slate-600 hover:border-slate-500'
                }`}
              >
                <h3 className="text-xl font-bold text-white mb-2">Corporate</h3>
                <p className="text-gray-300 text-sm">Professional</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
