import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getProfile, updateProfile } from '../services/api';

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('profile');
  
  // Profile Form
  const [profileData, setProfileData] = useState({
    name: '',
    bio: '',
    github: '',
    linkedin: '',
    twitter: ''
  });
  
  // Skills Form
  const [skills, setSkills] = useState({
    frontend: '',
    backend: '',
    devops: '',
    tools: ''
  });
  
  // Project Form
  const [project, setProject] = useState({
    title: '',
    description: '',
    techStack: '',
    repoLink: '',
    liveLink: ''
  });

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await getProfile();
      setUser(response.data);
      
      // Set profile data
      if (response.data.profile) {
        setProfileData({
          name: response.data.profile.name || '',
          bio: response.data.profile.bio || '',
          github: response.data.profile.socials?.github || '',
          linkedin: response.data.profile.socials?.linkedin || '',
          twitter: response.data.profile.socials?.twitter || ''
        });
      }
      
      // Set skills data
      if (response.data.skills) {
        setSkills({
          frontend: response.data.skills.frontend?.join(', ') || '',
          backend: response.data.skills.backend?.join(', ') || '',
          devops: response.data.skills.devops?.join(', ') || '',
          tools: response.data.skills.tools?.join(', ') || ''
        });
      }
      
      setLoading(false);
    } catch (error) {
      console.error('Error fetching profile:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      }
    }
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateProfile({
        profile: {
          name: profileData.name,
          bio: profileData.bio,
          socials: {
            github: profileData.github,
            linkedin: profileData.linkedin,
            twitter: profileData.twitter
          }
        }
      });
      alert('Profile updated successfully!');
      fetchProfile();
    } catch (error) {
      alert('Failed to update profile');
    }
  };

  const handleSkillsSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateProfile({
        skills: {
          frontend: skills.frontend.split(',').map(s => s.trim()).filter(s => s),
          backend: skills.backend.split(',').map(s => s.trim()).filter(s => s),
          devops: skills.devops.split(',').map(s => s.trim()).filter(s => s),
          tools: skills.tools.split(',').map(s => s.trim()).filter(s => s)
        }
      });
      alert('Skills updated successfully!');
      fetchProfile();
    } catch (error) {
      alert('Failed to update skills');
    }
  };

  const handleProjectSubmit = async (e) => {
    e.preventDefault();
    try {
      const newProject = {
        title: project.title,
        description: project.description,
        techStack: project.techStack.split(',').map(s => s.trim()).filter(s => s),
        repoLink: project.repoLink,
        liveLink: project.liveLink
      };
      
      const updatedProjects = [...(user.projects || []), newProject];
      
      await updateProfile({ projects: updatedProjects });
      alert('Project added successfully!');
      setProject({ title: '', description: '', techStack: '', repoLink: '', liveLink: '' });
      fetchProfile();
    } catch (error) {
      alert('Failed to add project');
    }
  };

  const handleTemplateChange = async (templateId) => {
    try {
      await updateProfile({ templateId });
      alert('Template updated successfully!');
      fetchProfile();
    } catch (error) {
      alert('Failed to update template');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) return <div style={{ padding: '20px' }}>Loading...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <h1>Dashboard</h1>
        <div>
          <a href={`/${user.username}`} target="_blank" rel="noopener noreferrer" style={{ marginRight: '10px' }}>
            View Portfolio
          </a>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>

      {/* Tabs */}
      <div style={{ borderBottom: '2px solid #ddd', marginBottom: '20px' }}>
        <button 
          onClick={() => setActiveTab('profile')}
          style={{ 
            padding: '10px 20px', 
            border: 'none', 
            background: activeTab === 'profile' ? '#007bff' : 'transparent',
            color: activeTab === 'profile' ? 'white' : 'black',
            cursor: 'pointer'
          }}
        >
          Profile
        </button>
        <button 
          onClick={() => setActiveTab('projects')}
          style={{ 
            padding: '10px 20px', 
            border: 'none', 
            background: activeTab === 'projects' ? '#007bff' : 'transparent',
            color: activeTab === 'projects' ? 'white' : 'black',
            cursor: 'pointer'
          }}
        >
          Projects
        </button>
        <button 
          onClick={() => setActiveTab('skills')}
          style={{ 
            padding: '10px 20px', 
            border: 'none', 
            background: activeTab === 'skills' ? '#007bff' : 'transparent',
            color: activeTab === 'skills' ? 'white' : 'black',
            cursor: 'pointer'
          }}
        >
          Skills
        </button>
        <button 
          onClick={() => setActiveTab('template')}
          style={{ 
            padding: '10px 20px', 
            border: 'none', 
            background: activeTab === 'template' ? '#007bff' : 'transparent',
            color: activeTab === 'template' ? 'white' : 'black',
            cursor: 'pointer'
          }}
        >
          Template
        </button>
      </div>

      {/* Profile Tab */}
      {activeTab === 'profile' && (
        <div>
          <h2>Edit Profile</h2>
          <form onSubmit={handleProfileSubmit}>
            <input
              type="text"
              placeholder="Your Name"
              value={profileData.name}
              onChange={(e) => setProfileData({...profileData, name: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
                        {/* Profile Picture Upload */}
            <div style={{ margin: '20px 0' }}>
              <label style={{ display: 'block', marginBottom: '10px' }}>
                Profile Picture
              </label>
              {user.profile?.avatar && (
                <img 
                  src={user.profile.avatar} 
                  alt="Profile" 
                  style={{ 
                    width: '150px', 
                    height: '150px', 
                    borderRadius: '50%', 
                    objectFit: 'cover',
                    marginBottom: '10px'
                  }} 
                />
              )}
              <input
                type="file"
                accept="image/*"
                onChange={handleProfilePictureUpload}
                style={{ display: 'block' }}
              />
            </div>
            
            <textarea
              placeholder="Bio"
              value={profileData.bio}
              onChange={(e) => setProfileData({...profileData, bio: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0', minHeight: '100px' }}
            />
            <input
              type="url"
              placeholder="GitHub URL"
              value={profileData.github}
              onChange={(e) => setProfileData({...profileData, github: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <input
              type="url"
              placeholder="LinkedIn URL"
              value={profileData.linkedin}
              onChange={(e) => setProfileData({...profileData, linkedin: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <input
              type="url"
              placeholder="Twitter URL"
              value={profileData.twitter}
              onChange={(e) => setProfileData({...profileData, twitter: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <button type="submit" style={{ padding: '10px 20px' }}>Save Profile</button>
          </form>
        </div>
      )}

      {/* Projects Tab */}
      {activeTab === 'projects' && (
        <div>
          <h2>Add Project</h2>
          <form onSubmit={handleProjectSubmit}>
            <input
              type="text"
              placeholder="Project Title"
              value={project.title}
              onChange={(e) => setProject({...project, title: e.target.value})}
              required
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <textarea
              placeholder="Project Description"
              value={project.description}
              onChange={(e) => setProject({...project, description: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0', minHeight: '100px' }}
            />
            <input
              type="text"
              placeholder="Tech Stack (comma separated: React, Node.js, MongoDB)"
              value={project.techStack}
              onChange={(e) => setProject({...project, techStack: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <input
              type="url"
              placeholder="GitHub Repository URL"
              value={project.repoLink}
              onChange={(e) => setProject({...project, repoLink: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <input
              type="url"
              placeholder="Live Demo URL"
              value={project.liveLink}
              onChange={(e) => setProject({...project, liveLink: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            <button type="submit" style={{ padding: '10px 20px' }}>Add Project</button>
          </form>

          <h3 style={{ marginTop: '30px' }}>Your Projects</h3>
          {user.projects && user.projects.length > 0 ? (
            user.projects.map((proj, index) => (
              <div key={index} style={{ border: '1px solid #ddd', padding: '15px', margin: '10px 0' }}>
                <h4>{proj.title}</h4>
                <p>{proj.description}</p>
                <p><strong>Tech:</strong> {proj.techStack?.join(', ')}</p>
                {proj.repoLink && <a href={proj.repoLink} target="_blank" rel="noopener noreferrer">GitHub</a>}
                {proj.liveLink && <a href={proj.liveLink} target="_blank" rel="noopener noreferrer" style={{ marginLeft: '10px' }}>Live Demo</a>}
              </div>
            ))
          ) : (
            <p>No projects added yet.</p>
          )}
        </div>
      )}

      {/* Skills Tab */}
      {activeTab === 'skills' && (
        <div>
          <h2>Edit Skills</h2>
          <form onSubmit={handleSkillsSubmit}>
            <label>Frontend (comma separated)</label>
            <input
              type="text"
              placeholder="React, JavaScript, HTML, CSS"
              value={skills.frontend}
              onChange={(e) => setSkills({...skills, frontend: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            
            <label>Backend (comma separated)</label>
            <input
              type="text"
              placeholder="Node.js, Express, MongoDB"
              value={skills.backend}
              onChange={(e) => setSkills({...skills, backend: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            
            <label>DevOps (comma separated)</label>
            <input
              type="text"
              placeholder="Docker, AWS, CI/CD"
              value={skills.devops}
              onChange={(e) => setSkills({...skills, devops: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            
            <label>Tools (comma separated)</label>
            <input
              type="text"
              placeholder="Git, VS Code, Postman"
              value={skills.tools}
              onChange={(e) => setSkills({...skills, tools: e.target.value})}
              style={{ width: '100%', padding: '10px', margin: '10px 0' }}
            />
            
            <button type="submit" style={{ padding: '10px 20px' }}>Save Skills</button>
          </form>
        </div>
      )}

      {/* Template Tab */}
      {activeTab === 'template' && (
        <div>
          <h2>Choose Template</h2>
          <p style={{ marginBottom: '20px', color: '#666' }}>
            Current Template: <strong>{user.templateId || 'minimalist'}</strong>
          </p>
          
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px' }}>
            <div 
              onClick={() => handleTemplateChange('minimalist')}
              style={{ 
                border: user.templateId === 'minimalist' ? '3px solid #007bff' : '1px solid #ddd',
                padding: '20px',
                cursor: 'pointer',
                borderRadius: '8px',
                textAlign: 'center'
              }}
            >
              <h3>Minimalist</h3>
              <p style={{ color: '#666', fontSize: '14px' }}>Clean & Simple</p>
            </div>
            
            <div 
              onClick={() => handleTemplateChange('cyberpunk')}
              style={{ 
                border: user.templateId === 'cyberpunk' ? '3px solid #007bff' : '1px solid #ddd',
                padding: '20px',
                cursor: 'pointer',
                borderRadius: '8px',
                textAlign: 'center',
                background: '#0a0a0a',
                color: '#00ff9f'
              }}
            >
              <h3>Cyberpunk</h3>
              <p style={{ color: '#ff00ff', fontSize: '14px' }}>Dark & Neon</p>
            </div>
            
            <div 
              onClick={() => handleTemplateChange('corporate')}
              style={{ 
                border: user.templateId === 'corporate' ? '3px solid #007bff' : '1px solid #ddd',
                padding: '20px',
                cursor: 'pointer',
                borderRadius: '8px',
                textAlign: 'center',
                background: '#2c3e50',
                color: 'white'
              }}
            >
              <h3>Corporate</h3>
              <p style={{ color: '#ecf0f1', fontSize: '14px' }}>Professional</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
