import React from 'react';
import './LoadingScreen.css';

function LoadingScreen() {
  return (
    <div className="loading-screen-particles">
      {/* Animated Background Particles */}
      <div className="particles">
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
      </div>

      <div className="loading-content">
        {/* Glowing Logo */}
        <div className="glowing-logo">
          <div className="logo-outer-ring"></div>
          <div className="logo-middle-ring"></div>
          <div className="logo-inner-circle">
            <span className="logo-cf">CF</span>
          </div>
        </div>

        {/* Title with Glitch Effect */}
        <h1 className="glitch-title" data-text="CodeFolio">CodeFolio</h1>

        {/* Progress Bar */}
        <div className="neon-progress-bar">
          <div className="neon-progress-fill"></div>
        </div>

        {/* Loading Text */}
        <p className="loading-status">Initializing your workspace...</p>
      </div>
    </div>
  );
}

export default LoadingScreen;
