import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container } from 'react-bootstrap';
import { getProfile } from '../services/api';
import { useDarkMode } from '../DarkModeContext';
import './Dashboard.css';

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentStep, setCurrentStep] = useState(1);
  const { darkMode, toggleDarkMode } = useDarkMode();

  useEffect(() => {
    fetchProfile();
    // eslint-disable-next-line
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await getProfile();
      setUser(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching profile:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="dashboard-dark">
        <Container className="text-center" style={{ paddingTop: '100px' }}>
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </Container>
      </div>
    );
  }

    return (
    <div className={`min-vh-100 ${darkMode ? 'dashboard-dark-mode' : 'bg-light'}`}>
      {/* Header */}
      <div className="bg-white shadow-sm mb-4">
        <Container className="py-3">
          <Row className="align-items-center">
            <Col>
              <h1 className="h3 mb-0">Dashboard</h1>
            </Col>
            <Col xs="auto">
              <a 
                href={`/${user.username}`} 
                target="_blank" 
                rel="noopener noreferrer"
                className="btn btn-outline-primary me-2"
              >
                View Portfolio
              </a>
              <Button 
                variant={darkMode ? 'light' : 'dark'} 
                onClick={toggleDarkMode}
                className="me-2"
              >
                {darkMode ? '‚òÄÔ∏è' : 'üåô'}
              </Button>
              <Button variant="danger" onClick={handleLogout}>
                Logout
              </Button>
            </Col>
          </Row>
        </Container>
      </div>

      
       
      {/* Progress Bar */}
      <Container>
        <div className="progress-bar-container">
          <div className="progress-steps">
            <div className="progress-step">
              <span className={`step-circle ${currentStep >= 1 ? 'active' : ''} ${currentStep > 1 ? 'completed' : ''}`}>
                {currentStep > 1 ? '‚úì' : '1'}
              </span>
              <span className="step-label">Profile Setup</span>
            </div>
            <div className="progress-step">
              <span className={`step-circle ${currentStep >= 2 ? 'active' : ''} ${currentStep > 2 ? 'completed' : ''}`}>
                {currentStep > 2 ? '‚úì' : '2'}
              </span>
              <span className="step-label">Add Projects</span>
            </div>
            <div className="progress-step">
              <span className={`step-circle ${currentStep >= 3 ? 'active' : ''} ${currentStep > 3 ? 'completed' : ''}`}>
                {currentStep > 3 ? '‚úì' : '3'}
              </span>
              <span className="step-label">Choose Template</span>
            </div>
            <div className="progress-step">
              <span className={`step-circle ${currentStep >= 4 ? 'active' : ''} ${currentStep > 4 ? 'completed' : ''}`}>
                {currentStep > 4 ? '‚úì' : '4'}
              </span>
              <span className="step-label">Customize</span>
            </div>
            <div className="progress-step">
              <span className={`step-circle ${currentStep >= 5 ? 'active' : ''} ${currentStep > 5 ? 'completed' : ''}`}>
                {currentStep > 5 ? '‚úì' : '5'}
              </span>
              <span className="step-label">Publish</span>
            </div>
          </div>
        </div>
      </Container>

      {/* Main Content */}
      <Container className="dashboard-main">
        <div className="text-center">
          <h1 className="dashboard-title">Choose Your Path</h1>
          <p className="dashboard-subtitle">
            What type of portfolio do you want to create?
          </p>
        </div>

        {/* Option Cards */}
        <div className="cards-grid">
          {/* Developer Portfolio */}
          <div className="option-card" onClick={() => setCurrentStep(2)}>
            <div className="card-icon">
              üíª
            </div>
            <h3 className="card-title">Developer Portfolio</h3>
            <p className="card-description">
              Showcase your coding projects, skills, and technical expertise
            </p>
            <div className="card-arrow">‚Üí</div>
          </div>

          {/* Designer Portfolio */}
          <div className="option-card" onClick={() => setCurrentStep(2)}>
            <div className="card-icon green">
              üé®
            </div>
            <h3 className="card-title">Designer Portfolio</h3>
            <p className="card-description">
              Display your creative work, designs, and artistic projects
            </p>
            <div className="card-arrow">‚Üí</div>
          </div>

          {/* Business Portfolio */}
          <div className="option-card" onClick={() => setCurrentStep(2)}>
            <div className="card-icon purple">
              üíº
            </div>
            <h3 className="card-title">Business Portfolio</h3>
            <p className="card-description">
              Professional portfolio for consultants and entrepreneurs
            </p>
            <div className="card-arrow">‚Üí</div>
          </div>
        </div>

        {/* Bottom Actions */}
        <div className="bottom-actions">
          <button className="btn-secondary-dark">
            ‚Üê Back
          </button>
          <button className="btn-secondary-dark">
            Skip ‚Üí
          </button>
        </div>
      </Container>
    </div>
  );
}

export default Dashboard;
